BytecodeOP_WriteRAM:
	MOVEA.l	rBytecode_PC, A0
	SWAP	D0
	MOVE.w	(A0)+, D0
	MOVE.w	(A0)+, D1
	MOVE.l	A0, rBytecode_PC
	MOVEA.l	D0, A0
	MOVE.w	D1, (A0)
	RTS
	
BytecodeOP_RunFunc:
	MOVEA.l	rBytecode_PC, A0
	SWAP	D0
	MOVE.w	(A0)+, D0
	MOVE.l	A0, rBytecode_PC
	MOVEA.l	D0, A0
	JMP	(A0)
	
BytecodeOP_Jump:
	MOVEA.l	rBytecode_PC, A0
	SWAP	D0
	MOVE.w	(A0), D0
	MOVE.l	D0, rBytecode_PC
	RTS

BytecodeOP_JumpEQ:
	TST.b	rBytecode_Ret
	BEQ.b	BytecodeOP_Jump
	ADDQ.l	#2, rBytecode_PC
