; Object Parameters:
; $A-$B: X Pos in Textbox | $C-$D: Y Pos in Textbox
; $10-$15: Textbox config
; $26-$27: Wait Timer
; $32-$35: Cutscene Pointer
Cutscene_ControllerObj:
	CLR.w	D0
	MOVE.b	rOnePlayer_CurCutscene, D0
	LSL.w	#2, D0
	LEA	Cutscene_CutLookupTbl, A1
	MOVE.l	(A1,D0.w), $32(A0)
	JSR	ObjSys_UpdateObjNextOpTimer
	JSR	GetMainControllerHeld
	ANDI.b	#$F0, D0
	BNE.w	CutCmd_End
	TST.w	$26(A0)
	BEQ.w	@StepCutscene
	SUBQ.w	#1, $26(A0)
	RTS
@StepCutscene:
	; Grab a byte from the cutscene pointer (A2), and store the increased pointer
	MOVEA.l	$32(A0), A2
	CLR.w	D0
	MOVE.b	(A2)+, D0
	MOVE.l	A2, $32(A0)
	
	; If the hightest bit isn't set, branch to E26C
	OR.b	D0, D0
	BPL.w	CutCmd_WriteChar
	
	; If the highest bit was set, run code depending on it
	ANDI.b	#$7F, D0
	LSL.w	#2, D0
	MOVEA.l	@Commands(PC,D0.w), A3
	
	; Get the next byte (argument to upcoming function call?)
	CLR.w	D0
	MOVE.b	(A2)+, D0
	MOVE.l	A2, $32(A0)
	JMP	(A3)
@Commands:
	dc.l	CutCmd_End
	dc.l	CutCmd_MakeTxtbox
	dc.l	CutCmd_ClearTxtbox
	dc.l	CutCmd_Wait
	dc.l	CutCmd_PlayArleAnim
	dc.l	CutCmd_PlayOpponentAnim
	dc.l	CutCmd_NewLine
	dc.l	loc_0000E1A0 
	dc.l	NULL 
	dc.l	CutCmd_Whitespace
	dc.l	SndDrv_PlayVoice 
	
CutCmd_End:
	BSR.w	loc_0000E210
	JSR	ObjSys_UpdateObjNextOpTimer
	CLR.b	rBytecode_StopRun
	JMP	loc_00002AF2
	
CutCmd_NewLine:
	SUBQ.l	#1, $32(A0)
CutCmd_NewLine2:
; Preform newline in textbox (if out of bounds, set to start of textbox
	CLR.w	$A(A0)
	ADDQ.w	#1, $C(A0)
	MOVE.w	$C(A0), D0
	CMP.w	$10(A0), D0
	BCS.w	loc_0000E19E
	CLR.w	$C(A0)
loc_0000E19E:
	RTS
	
loc_0000E1A0:
	SUBQ.l	#1, $32(A0)
	CLR.w	$A(A0)
	CLR.w	$C(A0)
	BSR.w	loc_0000E22E
	ORI.w	#$8E00, D0
	SWAP	D0
	JMP	Video_QueueBgMapSpecial
	
CutCmd_MakeTxtbox:
	; Make copies of argument
	MOVE.w	D0, D1
	MOVE.w	D0, D2
	
	; Get Lower 4 Bits of argument
	ANDI.b	#$0F, D0
	
	; Get bits 4-6 of argument
	LSR.b	#4, D1
	ANDI.b	#7, D1
	
	; Store them 
	MOVE.w	D0, $E(A0)
	MOVE.w	D1, $10(A0)
	
	; Get Bits 6-7 and store them
	LSR.w	#6, D2
	ANDI.b	#2, D2
	MOVE.w	D2, $14(A0)
	
	; Get next two arguments
	MOVE.b	(A2)+, $12(A0)
	MOVE.b	(A2)+, $13(A0)
	
	; Store updated pointer
	MOVE.l	A2, $32(A0)
	
	; ?
	CLR.w	$A(A0)
	CLR.w	$C(A0)
	
	; Gets the stored word from $12(A0)
	; Puts it in the upperhalf of D0
	; Gets the stored word from $10(A0)
	; LSL's by 4 and stores in the lower half of D0
	BSR.w	loc_0000E22E
	
	; ?
	ORI.w	#$8C00, D0
	SWAP	D0
	
	; ?
	JSR	Video_QueueBgMapSpecial
	
	; ?
	BSR.w	loc_0000E2F2
	
	; ?
	MOVE.b	#$FF, $7(A0)
	RTS
	
CutCmd_ClearTxtbox:
	SUBQ.l	#1, $32(A0)
loc_0000E210:
	TST.b	$7(A0)
	BNE.w	loc_0000E21A
	RTS
loc_0000E21A:
	CLR.b	$7(A0)
	BSR.w	loc_0000E22E
	ORI.w	#$8D00, D0
	SWAP	D0
	JMP	Video_QueueBgMapSpecial
loc_0000E22E:
	MOVE.w	$12(A0), D0
	SWAP	D0
	CLR.w	D0
	MOVE.w	$10(A0), D0
	LSL.w	#4, D0
	OR.w	$E(A0), D0
	RTS
	
CutCmd_Whitespace:
