	dbf D0, @OuterLoop
	bsr.w Option_InitVramPlane
	move.w #$5A0, D5
	move.w #$A500, D6
	lea (@Warning).l, A1
	bsr.w loc_0001D6C4
	move.w #$796, D5
	move.w #$8500, D6
